DELIMITER $$
CREATE TRIGGER `before_insert_product` BEFORE INSERT ON `product` FOR EACH ROW BEGIN
    DECLARE min_id INT;

    -- Tìm ID nhỏ nhất chưa được sử dụng
    SELECT MIN(t1.id + 1) INTO min_id
    FROM (
        SELECT 0 AS id
        UNION
        SELECT id FROM product
    ) AS t1
    LEFT JOIN product t2
    ON t1.id + 1 = t2.id
    WHERE t2.id IS NULL;

    -- Nếu tìm thấy ID trống, gán nó cho NEW.id
    IF NEW.id IS NULL AND min_id IS NOT NULL THEN
        SET NEW.id = min_id;
    END IF;
END
$$
DELIMITER ;

